import React, { useState } from 'react';

const SearchBooks = () => {
  const [query, setQuery] = useState("");
  const [books, setBooks] = useState([]);

  const searchBooks = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_SERVER_URL}/search/${query}`);

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      setBooks(data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const saveBook = async (book) => {
    try {
      const response = await fetch(`${process.env.REACT_APP_SERVER_URL}/books`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(book),
      });

      if (!response.ok) {
        throw new Error('Failed to save the book');
      }

      const savedBook = await response.json();
      console.log('Book saved:', savedBook);
    } catch (error) {
      console.error('Error saving book:', error);
    }
  };

  return (
    <div
      style={{
        textAlign: 'center',
        padding: '20px',
        backgroundImage: `url(${process.env.PUBLIC_URL}/library.jpg)`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        color: 'white', // Ensures text is readable over the image
      }}
    >
      <h1 style={{ marginBottom: '20px' }}>Milestone Library</h1>

      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search for books"
        style={{ padding: '10px', width: '300px', marginBottom: '20px' }}
      />
      
      <button onClick={searchBooks} style={{ padding: '10px 20px' }}>Search</button>

      <div>
        {books.length > 0 ? (
          books.map((book, index) => (
            <div key={index} style={{ marginTop: '20px' }}>
              <h3 onClick={() => saveBook(book)} style={{ cursor: 'pointer', color: 'yellow' }}>
                {book.title}
              </h3>
              <p>ISBN: {book.isbn}</p>
              <p>Key: {book.key}</p>
              <p>Authors: {book.authors ? book.authors.join(', ') : 'Unknown'}</p>
              {book.cover_i && (
                <img src={`https://covers.openlibrary.org/b/id/${book.cover_i}-L.jpg`} alt={`Cover of ${book.title}`} />
              )}
              <button onClick={() => saveBook(book)} style={{ padding: '10px 20px', marginTop: '10px' }}>
                Save for Later
              </button>
            </div>
          ))
        ) : (
          <p>No books found</p>
        )}
      </div>
    </div>
  );
};

export default SearchBooks;
